/* eslint-disable no-await-in-loop */
const { By, until } = require('selenium-webdriver');
const { resolve } = require('path');
const Test = require('./common/Test');
require('chromedriver');

describe('settings account page', () => {
  jest.setTimeout(10000);

  let test;
  let driver;
  let user;

  beforeEach(async () => {
    test = new Test();
    // load the default page
    driver = await test.getDriver();
    user = await test.loginUser('settingsAccountUser');
    await driver.get(`${Test.getApp()}/settings`);
  }, 10000);

  afterEach(async () => {
    // delete the user
    await test.removeUser();
    // close the driver
    await test.cleanUp();
  }, 15000);

  /// ///////////////////
  // Account Information
  /// ///////////////////

  it('shows the account information @network @accessibility', async () => {
    driver.wait(until.elementLocated(By.css('h2')), 5000);
    const accountInfo = (await driver.findElements(By.css('form')))[0];
    expect(await accountInfo.findElement(By.css('h3')).getText()).toEqual(
      'Account Information'
    );
  });

  it('by default the user initials are shown as the avatar', async () => {
    driver.wait(until.elementLocated(By.css('h2')), 5000);
    const avatar = driver.findElement(By.className('initials'));
    await driver.wait(until.elementTextIs(avatar, 'TU'), 5000);
    expect(await avatar.getText()).toEqual('TU');
    expect(
      await driver.findElement(By.className('circle')).getAttribute('style')
    ).toEqual('');
  });

  it('allows us to upload an avatar @network @accessibility', async () => {
    driver.wait(until.elementLocated(By.css('h2')), 5000);
    driver
      .findElement(By.css('[type="file"]'))
      .sendKeys(`${resolve('./')}/selenium/DSC_0630.JPG`);
    test.waitUntilNotPresent(By.id('initials'));
    expect(await driver.findElements(By.id('initials'))).toHaveLength(0);
    expect(
      await driver.findElement(By.className('circle')).getAttribute('style')
    ).toMatch(/data:image\/jpeg;base64,\/9j\/4AAQSkZJRgABAQAAAQABAAD\/4g/);
  });

  it('allows us to replace an avatar @network', async () => {
    await user.setAvatar(
      'data:image/jpeg;base64,'
    );
    await driver.navigate().refresh();
    await driver.wait(until.elementLocated(By.css('h2')), 5000);
    expect(await driver.findElements(By.className('initials'))).toHaveLength(0);
    expect(
      await driver.findElement(By.className('circle')).getAttribute('style')
    ).toEqual(
      'background-image: url("data:image/jpeg;base64,");'
    );
    await driver
      .findElement(By.css('[type="file"]'))
      .sendKeys(`${resolve('./')}/selenium/DSC_0630.JPG`);
    expect(
      await driver.findElement(By.className('circle')).getAttribute('style')
    ).toMatch(/data:image\/jpeg;base64,\/9j\/4AAQSkZJRgABAQAAAQABAAD\//);
  });

  it('displays the username as disabled', async () => {
    const username = driver.wait(
      until.elementLocated(By.id('formUsername')),
      5000
    );
    await test.waitUntilInputFilled(By.id('formUsername'));
    expect(await username.getAttribute('value')).toEqual('settingsAccountUser');
    expect(await username.getAttribute('disabled')).toEqual('true');
  });

  it('displays the email', async () => {
    const email = driver.wait(until.elementLocated(By.id('formEmail')), 5000);
    await test.waitUntilInputFilled(By.id('formEmail'));
    expect(await email.getAttribute('value')).toEqual(
      'settingsAccountUser@example.org'
    );
    expect(await email.getAttribute('disabled')).toBeNull();
  });

  it('shows error when you update with blank information @network @accessibility', async () => {
    driver.wait(until.elementLocated(By.css('h2')), 5000);
    const accountInfo = (await driver.findElements(By.css('form')))[0];
    const feedback = await accountInfo.findElements(
      By.className('invalid-feedback')
    );
    expect(feedback).toHaveLength(2);
    for (let i = 0; i < feedback.length; i++) {
      expect(await feedback[i].getText()).toEqual('');
      expect(await feedback[i].isDisplayed()).toBeFalsy();
    }
    driver.findElement(By.id('formEmail')).clear();
    driver.findElement(By.id('saveAccountInformationButton')).click();
    expect(await feedback[0].getText()).toEqual('');
    expect(await feedback[0].isDisplayed()).toBeFalsy();
    expect(await feedback[1].getText()).toEqual(
      'Please provide a valid email.'
    );
    for (let i = 1; i < feedback.length; i++) {
      expect(await feedback[i].isDisplayed()).toBeTruthy();
    }
  });

  async function updateAccountInfo() {
    await driver.findElement(By.id('saveAccountInformationButton')).click();
    const success = driver.wait(
      until.elementLocated(By.className('alert-success')),
      5000
    );
    expect(await success.getText()).toEqual('Account Information Updated');
    await Test.sleep(5000);
    expect(
      await driver.findElements(By.className('alert-success'))
    ).toHaveLength(0);
    driver.navigate().refresh();
  }

  it('allows updating the email @network @accessibility', async () => {
    let email = driver.wait(until.elementLocated(By.id('formEmail')), 5000);
    await test.waitUntilInputFilled(By.id('formEmail'));
    await email.sendKeys('o');
    expect(await email.getAttribute('value')).toEqual(
      'settingsAccountUser@example.orgo'
    );
    await updateAccountInfo();
    email = driver.wait(until.elementLocated(By.id('formEmail')), 5000);
    await test.waitUntilInputFilled(By.id('formEmail'));
    expect(await email.getAttribute('value')).toEqual(
      'settingsaccountuser@example.orgo'
    );
  });

  it('allows updating but keeping the email the same', async () => {
    const save = driver.wait(
      until.elementLocated(By.id('saveAccountInformationButton')),
      5000
    );
    await save.click();
    const success = driver.wait(
      until.elementLocated(By.className('alert-success')),
      5000
    );
    expect(await success.getText()).toEqual('Account Information Updated');
  });

  async function submitWithBadEmail() {
    const email = driver.wait(until.elementLocated(By.id('formEmail')), 5000);
    await test.waitUntilInputFilled(By.id('formEmail'));
    email.sendKeys('1');
    await driver.findElement(By.id('saveAccountInformationButton')).click();
    const danger = driver.wait(
      until.elementLocated(By.className('alert-danger')),
      5000
    );
    return { danger, email };
  }

  it('does not allow updating to an invalid email @network @accessibility', async () => {
    const { danger } = await submitWithBadEmail();
    expect(await danger.getText()).toEqual('Please include a valid email');
  });

  it('does not allow updating to a duplicate email @network @accessibility', async () => {
    const test1 = new Test();
    try {
      test1.addUser('settingsUser1');
      const email = driver.wait(until.elementLocated(By.id('formEmail')), 5000);
      await test.waitUntilInputFilled(By.id('formEmail'));
      email.clear();
      email.sendKeys('settingsUser1@example.org');
      await driver.findElement(By.id('saveAccountInformationButton')).click();
      const danger = driver.wait(
        until.elementLocated(By.className('alert-danger')),
        5000
      );
      expect(await danger.getText()).toEqual(
        'This email is already in our system.'
      );
    } finally {
      await test1.removeUser();
    }
  });

  it('account information error message goes away once success is had @network @accessibility', async () => {
    const { email } = await submitWithBadEmail();
    await email.clear();
    await email.sendKeys('email@example.org');
    await driver.findElement(By.id('saveAccountInformationButton')).click();
    const success = await driver.wait(
      until.elementLocated(By.className('alert-success')),
      5000
    );
    expect(await success.getText()).toEqual('Account Information Updated');
    expect(
      await driver.findElements(By.className('alert-danger'))
    ).toHaveLength(0);
  });
});
